'use client'

import React from 'react'
import { cn } from '@/utils/ui'
import { getImage } from 'astro:assets'
import type { MediaProps } from './types'

// A base64 encoded image to use as a placeholder while the image is loading
const placeholderBlur =
    'data:image/png;base64,'

export const ImageMedia: React.FC<MediaProps> = async props => {
    const {
        imgClassName,
        pictureClassName,
        resource,
        loading,
        decoding,
        fetchPriority,
        fill,
    } = props
    if (!resource || typeof resource === 'string') {
        return null
    }

    const { srcSet } = await getImage({
        src: resource.url,
        width: resource.width,
        height: resource.height,
    })

    const sizes =
        srcSet.values.length > 0
            ? srcSet.values
                  .map(
                      ({ transform }) =>
                          `(max-width: ${transform.width}px) ${transform.width * 2}w`
                  )
                  .join(', ')
            : '(max-width: 768px) 100vw, 768px'
    return (
        <picture className={cn(props.className, pictureClassName)}>
            <img
                srcSet={srcSet.attribute}
                sizes={sizes}
                src={resource.url}
                width={resource.width}
                height={resource.height}
                loading={
                    loading ?? (fetchPriority === 'high' ? 'eager' : 'lazy')
                }
                decoding={decoding ?? 'async'}
                className={cn(imgClassName, {
                    'absolute inset-0 w-full h-full object-cover': fill,
                })}
                fetchPriority={fetchPriority}
            />
        </picture>
    )
}
